  render() {             
    return (
      // le return n'accepte qu'un seule tag 
      //donc tous ceque je veut a la fin doit etre dans un seule div 
      <div>
         <Header />
         <Section />
         <Footer />
      </div>
    );
  }
  }

export default App;
