import React,{Component} from "react";

class Items extends Component{
  render(){
    return(
      <div class="text-start px-4">
         <h2  class="fw-bold" >Show Checkboxes</h2>


         <input class="form-check-input mt-0 color=#198754 " type="checkbox" id="reseau1" name="reseau1" value="Facebook"/>
         <label for="reseau1"> Facebook</label><br/>
         <input class="form-check-input mt-0" type="checkbox" id="reseau2" name="reseau2" value="Instagrame"/>
         <label for="reseau2"> Instagrame</label><br/>
         <input class="form-check-input mt-0"  type="checkbox" id="reseau3" name="reseau3" value="Youtube"/>
         <label for="reseau3"> Youtube</label><br/><br/>
         <input  class="btn btn-success" type="submit" value="Submit"/>
      </div>
    );
  }
}
export default Items ;
