import React, { Component } from "react";

class TeamMember extends Component{
  constructor(){
    super()
    this.state ;
      }
   render (){ 
      return(
        <div className="col-md-4 col-sm-6 ">

         <div className="card">
              <div className="card-header">
                <img style={{maxWidth : '100%'}}src={this.state.info.img} alt=''/>
              </div>
              <div className="card-body"
              style={{backgroundColor : this.state.info.website ? 'white' : 'black'  }}>

                 <h2>{this.state.info.name}</h2>
                 <h5>{this.state.info.position}</h5>
                 <div>{this.state.info.phone}</div>
                 <div>{this.state.info.email  ? this.state.info.email  : 'Pas de compte mail'}</div>
                 <div>{this.state.info.website  }</div>
              </div>
         </div>

        </div>
    );
}
}
export default TeamMember  ;
